// Helper to validate categories against config
function validateCategories() {
  const categoryNames = Object.keys(CATEGORIES);
  const errors = [];

  if (categoryNames.length < CONFIG.numGroups) {
    errors.push(`Need at least ${CONFIG.numGroups} categories, but only have ${categoryNames.length}`);
  }

  for (const [name, items] of Object.entries(CATEGORIES)) {
    if (items.length < CONFIG.itemsPerGroup) {
      errors.push(`Category "${name}" has ${items.length} items, needs ${CONFIG.itemsPerGroup}`);
    }

    // Check for duplicates within category
    const seen = new Set();
    for (const item of items) {
      if (seen.has(item)) {
        errors.push(`Category "${name}" has duplicate: "${item}"`);
      }
      seen.add(item);
    }
  }

  return errors;
}
